import React, { Component } from "react";
import "./bar.css";
import Axios from "axios";
import ReactTable from "react-table";
import "react-table/react-table.css";

class bars extends Component {
  constructor(props) {
    super(props);
    this.state = {
      bar: [],
    };
  }

  componentDidMount = (props) => {
    Axios.get("/lawyersReporter").then((response) => {
      console.log(response.data);
      this.setState({
        laws: JSON.stringify(response.data),
      });
    });
  };
  /*
  componentDidMount = () => {
    const url = "/lawyersReporter";
    fetch(url, {
      method: "GET",
    })
      .then((response) => response.json())
      .then((bar) => {
        // console.log("bar", bar);
        this.setState({ bar: bar });
        console.log("laywers", bar);
      });
  };
  */

  render() {
    const { bar } = this.state;
    console.log(bar);
    const columns = [
      {
        Header: "ID",
        accessor: "employee_ID",
      },
      {
        Header: "First Name",
        accessor: "firstName",
      },
      {
        Header: "Last Name",
        accessor: "lastName",
      },
      {
        Header: "Grading",
        accessor: "barGrading",
      },
    ];

    return (
      <div className="breezy">
        <h2>Lawyers</h2>
        <ReactTable
          columns={columns}
          data={bar}
          defaultPageSize={7}
          pageSizeOptions={[3, 6]}
          showPagination={true}
        ></ReactTable>
        ;
      </div>
    );
  }
}

export default bars;
