import React, { Component } from "react";

class lawAdd extends Component {
  constructor(props) {
    super(props);
    this.state = {
      firstName: "",
      lastName: "",
      barGrading: "",
    };
  }
  render() {
    const { firstName, lastName, barGrading } = this.state;
    return (
      <div>
        <h1>Hello JONES</h1>
        <from>
          <label>First Name : </label>
          <input
            type="text"
            name="firstName"
            value={firstName}
            onChange={this.changeHandler}
          ></input>
          <br />
          <br />
          <label>Last Name : </label>
          <input
            type="text"
            name="lastName"
            value={lastName}
            onChange={this.changeHandler}
          ></input>
          <br />
          <br />
          <label>Grading : </label>
          <input
            type="Number"
            name="barGrading"
            value={barGrading}
            onChange={this.changeHandler}
          ></input>
          <br />
          <br />
          <input type="submit"></input>
        </from>
      </div>
    );
  }
}

export default lawAdd;
