import React, { Component } from "react";
import "./payinvoice.css";
import Axios from "axios";
import ReactTable from "react-table";
import "react-table/react-table.css";

class payinvoce extends Component {
  constructor(props) {
    super(props);
    this.state = {
      project_Name: "",
      invoice: [],
    };
  }
  changeHandler = (e) => {
    this.setState({ [e.target.name]: e.target.value });
  };

  submitHandler = (e) => {
    e.preventDefault();
    console.log(this.state);
    const find_company = this.state;
    console.log("addinfo", find_company);
    Axios.post("/sumReport", find_company).then((response) => {
      this.setState({
        invoice: response,
      });
    });
  };

  render() {
    const { invoice } = this.state;
    //console.log("invoice", payload);
    const { project_Name } = this.state;
    const columns = [
      {
        Header: "ID",
        accessor: "employeeID",
        sortable: false,
        style: {
          textAlign: "center",
        },
        width: 120,
        maxWidth: 150,
        minWidth: 70,
      },
      {
        Header: "Number of Hours",
        accessor: "hours",
        sortable: false,
        width: 200,
        style: {
          textAlign: "center",
        },
      },
      {
        Header: "Unit Price",
        accessor: "barGrading",
        sortable: false,
        width: 200,
        style: {
          textAlign: "center",
        },
      },
      {
        Header: "Cost",
        accessor: "cost",
        sortable: true,
        width: 170,
        maxWidth: 150,
        style: {
          textAlign: "center",
        },
      },
    ];

    return (
      <>
        <div className="all_body">
          <h3>
            <br />
            View Invonce for Company
            <br />
          </h3>
          <label className="lab_cname">Company Name : </label>
          <input
            id="2"
            className="inp_company"
            type="text"
            name="project_Name"
            value={project_Name}
            onChange={this.changeHandler}
          ></input>
          <br />
        </div>
        <div>
          <ReactTable
            columns={columns}
            data={invoice}
            defaultPageSize={7}
            noDataText={"click button above"}
            //showPagination={false}
            showPaginationTop={false}
            showPaginationBottom={false}
          />
        </div>
      </>
    );
  }
}

export default payinvoce;
