import React, { Component } from "react";
import "./lawtimesheet.css";
import Axios from "axios";
import ReactTable from "react-table";
import "react-table/react-table.css";

class lawtimesheet extends Component {
  constructor(props) {
    super(props);
    this.state = {
      timesheet: [],
    };
  }

  componentDidMount = () => {
    Axios.get("/reportsforalltIimeSheet").then((response) => {
      this.setState({
        timesheet: response.data,
      });
    });
  };

  render() {
    const { timesheet } = this.state;
    const columns = [
      {
        Header: "ID",
        accessor: "employee_ID",
        sortable: false,
        style: {
          textAlign: "center",
        },
        width: 70,
        maxWidth: 100,
        minWidth: 70,
      },
      {
        Header: "First Name",
        accessor: "firstName",
        sortable: false,
        width: 150,
      },
      {
        Header: "Last Name",
        accessor: "lastName",
        sortable: false,
        width: 150,
      },
      {
        Header: "Grading",
        accessor: "barGrading",
        sortable: true,
        width: 70,
        maxWidth: 100,
      },
      {
        Header: "Action",
        Cell: (props) => {
          return (
            <button
              className="del_butt"
              onClick={() => {
                //console.log("props", props);
                this.deleteRow(props.original.employee_ID);
              }}
            >
              Delete
            </button>
          );
        },
        width: 70,
        maxWidth: 70,
        minWidth: 70,
        sortable: false,
        filterable: false,
      },
    ];

    return <div>HELLO</div>;
  }
}

export default lawtimesheet;
