import React, { Component } from "react";
import "./payinvoice.css";
import Axios from "axios";
import ReactTable from "react-table";
import "react-table/react-table.css";

class payinvoce extends Component {
  constructor(props) {
    super(props);
    this.state = {
      payload: [],
    };
  }
  /*
  componentDidMount = () => {
    const project = { project_Name: "MTN" };
    Axios.post("./rangesumReport", project).then((response) =>
      this.setState({ payload: response.data.id })
    );
  };
  */
  componentDidMount = () => {
    // Simple POST request with a JSON body using axios
    const project_Name = { project_Name: "MTN" };
    Axios.post("./rangesumReport", project_Name).then((response) =>
      this.setState({ payload: response.data.id })
    );
  };

  render() {
    const columns = [
      {
        Header: "ID",
        accessor: "employeeID",
        sortable: false,
        style: {
          textAlign: "center",
        },
        width: 120,
        maxWidth: 150,
        minWidth: 70,
      },
      {
        Header: "Number of Hours",
        accessor: "hours",
        sortable: false,
        width: 200,
        style: {
          textAlign: "center",
        },
      },
      {
        Header: "Unit Price",
        accessor: "barGrading",
        sortable: false,
        width: 200,
        style: {
          textAlign: "center",
        },
      },
      {
        Header: "Cost",
        accessor: "cost",
        sortable: true,
        width: 170,
        maxWidth: 150,
        style: {
          textAlign: "center",
        },
      },
    ];

    return (
      <>
        <div className="all_body">
          <h3>
            <br />
            View Invonce for Company
          </h3>
        </div>
        <div>
          <ReactTable
            columns={columns}
            defaultPageSize={7}
            noDataText={"click button above"}
            //showPagination={false}
            showPaginationTop={false}
            showPaginationBottom={false}
          />
        </div>
      </>
    );
  }
}

export default payinvoce;
