import React, { Component } from "react";
import "./timeinput.css";
import Axios from "axios";

class timeinput extends Component {
  constructor(props) {
    super(props);
    this.state = {
      empoloyeeID: "",
      project_Name: "",
      p_Date: "",
      p_Start: "",
      p_End: "",
      clocktime: [],
    };
  }
  changeHandler = (e) => {
    this.setState({ [e.target.name]: e.target.value });
  };

  submitHandler = (e) => {
    e.preventDefault();
    //console.log(this.state);
    const add_time = this.state;
    //console.log("addtime", add_time);
    Axios.post("/inputlawyers", add_time).then((response) => {
      this.setState({
        clocktime: response,
      });
    });
  };

  render() {
    const { clocktime } = this.state;
    console.log(clocktime);
    const { empoloyeeID, project_Name, p_Date, p_Start, p_End } = this.state;
    return (
      <div>
        <h2>Add timesheet Record</h2>
        <br />
        <form onSubmit={this.submitHandler}>
          <div>
            <label className="lab_empID">Employee ID : </label>
            <input
              className="inp_empID"
              type="number"
              name="empoloyeeID"
              value={empoloyeeID}
              onChange={this.changeHandler}
            ></input>
            <br />
            <br />
            <label className="lab_pName">Project Name : </label>
            <input
              className="inp_pName"
              type="text"
              name="project_Name"
              value={project_Name}
              onChange={this.changeHandler}
            ></input>
            <br />
            <br />
            <label className="lab_pdate">Project Date : </label>
            <input
              className="inp_pdate"
              type="date"
              name="p_Date"
              placeholder="yyyy-mm-dd"
              value={p_Date}
              onChange={this.changeHandler}
            ></input>
            <br />
            <br />
            <label className="lab_pstart">Start Time : </label>
            <input
              className="inp_pstart"
              type="time"
              name="p_Start"
              value={p_Start}
              onChange={this.changeHandler}
            ></input>
            <br />
            <br />
            <label className="lab_pend">End Time : </label>
            <input
              className="inp_pend"
              type="time"
              name="p_End"
              value={p_End}
              onChange={this.changeHandler}
            ></input>
            <br />
          </div>
          <button type="submit">Submit</button>
        </form>
      </div>
    );
  }
}

export default timeinput;
