import React, { Component } from "react";
import "./timeinput.css";
import Axios from "axios";

class timeinput extends Component {
  constructor(props) {
    super(props);
    this.state = {
      empoloyeeID: "",
      project_Name: "",
      p_Date: "",
      p_Start: "",
      p_End: "",
      clockTime: [],
    };
  }
  changeHandler = (e) => {
    this.setState({ [e.target.name]: e.target.value });
  };

  render() {
    const { empoloyeeID, project_Name, p_Date, p_Start, p_End } = this.state;
    return (
      <div>
        <h2>Add timesheet Record</h2>
        <br />
        <form>
          <div>
            <label className="lab_empID"></label>
            <input
              className="inp_empID"
              type="number"
              value={empoloyeeID}
              onChange={this.changeHandler}
            ></input>
            <br />
            <label className="lab_pName"></label>
            <input
              className="inp_pName"
              type="text"
              value={project_Name}
              onChange={this.changeHandler}
            ></input>
            <br />
            <label className="lab_pdate"></label>
            <input
              className="inp_pdate"
              type="date"
              value={p_Date}
              onChange={this.changeHandler}
            ></input>
            <br />
            <label className="lab_pstart"></label>
            <input
              className="inp_pstart"
              type="date"
              value={p_Start}
              onChange={this.changeHandler}
            ></input>
            <br />
            <label className="lab_pend"></label>
            <input
              className="inp_pend"
              type="number"
              value={p_End}
              onChange={this.changeHandler}
            ></input>
            <br />
          </div>
        </form>
      </div>
    );
  }
}

export default timeinput;
