import React, { Component } from "react";
import "./bar.css";
import Axios from "axios";
import ReactTable from "react-table";
import "react-table/react-table.css";

class bars extends Component {
  constructor(props) {
    super(props);
    this.state = {
      bar: [],
    };
  }

  handleButtonClick = () => {
    Axios.get("/lawyersReporter").then((response) => {
      this.setState({
        bar: response.data[0],
      });
    });
  };
  deleteRow(employee_ID) {
    //console.log("employee_ID", employee_ID);
    const index = this.state.bar.findIndex((barr) => {
      return barr.employee_ID === employee_ID;
    });
    console.log("index", index);
  }

  render() {
    const { bar } = this.state;
    const columns = [
      {
        Header: "ID",
        accessor: "employee_ID",
        sortable: false,
        style: {
          textAlign: "center",
        },
        width: 70,
        maxWidth: 100,
        minWidth: 70,
      },
      {
        Header: "First Name",
        accessor: "firstName",
        sortable: false,
        width: 150,
      },
      {
        Header: "Last Name",
        accessor: "lastName",
        sortable: false,
        width: 150,
      },
      {
        Header: "Grading",
        accessor: "barGrading",
        sortable: true,
        width: 70,
        maxWidth: 100,
      },
      {
        Header: "Action",
        Cell: (props) => {
          return (
            <button
              className="del_butt"
              onClick={() => {
                //console.log("props", props);
                this.deleteRow(props.original.employee_ID);
              }}
            >
              Delete
            </button>
          );
        },
        width: 70,
        maxWidth: 70,
        minWidth: 70,
        sortable: false,
        filterable: false,
      },
    ];

    return (
      <div className="all_ground">
        <div></div>
        <div className="breezy">
          <h2>
            Click to view list of all lawyers and their hourly charge rate
          </h2>
          <button className="get_lawyers" onClick={this.handleButtonClick}>
            Available Lawyers
          </button>

          <ReactTable
            className="tableux"
            columns={columns}
            data={bar}
            defaultPageSize={5}
            noDataText={false}
            //showPagination={false}
            showPaginationTop={false}
            showPaginationBottom={false}
          ></ReactTable>
        </div>
      </div>
    );
  }
}

export default bars;
