import React, { Component } from "react";
import "./lawAdd.css";

class lawAdd extends Component {
  constructor(props) {
    super(props);
    this.state = {
      firstName: "",
      lastName: "",
      barGrading: "",
    };
  }

  changeHandler = (e) => {
    this.setState({ [e.target.name]: e.target.value });
  };

  submitHandler = (e) => {
    e.preventDefault();
    console.log(this.state);
  };

  render() {
    const { firstName, lastName, barGrading } = this.state;
    return (
      <div>
        <form onSubmit={this.submitHandler}>
          <div>
            <label className="lab_fname">First Name : </label>
            <input
              className="inp_fname"
              type="text"
              name="firstName"
              value={firstName}
              onChange={this.changeHandler}
            ></input>
            <br />
            <br />
            <label className="lab_lname">Last Name : </label>
            <input
              className="inp_lname"
              type="text"
              name="lastName"
              value={lastName}
              onChange={this.changeHandler}
            ></input>
            <br />
            <br />
            <label>Grading : </label>
            <input
              className="inp_num"
              type="Number"
              name="barGrading"
              value={barGrading}
              onChange={this.changeHandler}
            ></input>
            <br />
            <br />
          </div>

          <button type="submit">submit</button>
        </form>
      </div>
    );
  }
}

export default lawAdd;
