import React, { Component } from "react";
import "./bar.css";
import Axios from "axios";

class bars extends Component {
  constructor(props) {
    super(props);
    this.state = {
      bars: [],
    };
  }
  componentDidMount = (props) => {
    Axios.get("/lawyersReporter").then((response) => {
      console.log(response.data);
      this.setState({
        bars: JSON.stringify(response.data),
      });
    });
  };
  render() {
    return (
      <div>
        <h2>Lawyers</h2>
        <ul>
          {this.state.bar.map((bar) => (
            <li key={bar.employee_ID}>{bar.firstName}</li>
          ))}
        </ul>
      </div>
    );
  }
}

export default bars;
