import React, { Component } from "react";
import "./lawtimesheet.css";
import Axios from "axios";
import ReactTable from "react-table";
import "react-table/react-table.css";

class lawtimesheet extends Component {
  constructor(props) {
    super(props);
    this.state = {
      sheet: [],
    };
  }

  componentDidMount = () => {
    Axios.get("/reportsforalltIimeSheet").then((response) => {
      this.setState({
        sheet: response.data,
      });
    });
  };

  render() {
    const { sheet } = this.state;
    const columns = [
      {
        Header: "ID",
        accessor: "employeeID",
        sortable: false,
        style: {
          textAlign: "center",
        },
        width: 70,
        maxWidth: 100,
        minWidth: 70,
      },
      {
        Header: "ID",
        accessor: "barGrading",
        sortable: false,
        style: {
          textAlign: "center",
        },
        width: 70,
        maxWidth: 100,
        minWidth: 70,
      },
      {
        Header: "Date",
        accessor: "p_Date",
        sortable: false,
        width: 150,
      },
      {
        Header: "End Time",
        accessor: "p_End",
        sortable: false,
        width: 100,
      },
      {
        Header: "Start Time",
        accessor: "p_Start",
        sortable: true,
        width: 120,
        maxWidth: 150,
      },
      {
        Header: "Project Name",
        accessor: "project_Name",
        sortable: true,
        width: 150,
        maxWidth: 150,
      },
    ];

    return (
      <div className="all_render">
        <div classname="dj_feezy">
          <h2>Lawyers Timetable</h2>

          <ReactTable
            className="gambo"
            columns={columns}
            data={sheet}
            defaultPageSize={7}
            noDataText={"click button above"}
            //showPagination={false}
            showPaginationTop={false}
            showPaginationBottom={false}
          ></ReactTable>
        </div>
      </div>
    );
  }
}

export default lawtimesheet;
