import React, { Component } from "react";
//import Axios from "axios";
import "./law_report.css";

class law_report extends Component {
  constructor(props) {
    super(props);
    this.state = {
      laws: [],
      isLoaded: false,
    };
  }
  componentDidMount = (props) => {
    fetch("/lawyersReporter")
      .then((res) => res.json())
      .then((json) => {
        this.setState({
          isLoaded: true,
          laws: json,
        });
      });

    /*
    Axios.get("/lawyersReporter").then((response) => {
      //console.log(response.data.tem_c);
      console.log(response);
      this.setState({
        laws: response.data,
      });
    });
    */
  };

  render() {
    var { isLoaded, laws } = this.state;
    if (!isLoaded) {
      return <div>Loading ....</div>;
    } else {
      <div>data loaded</div>;
    }
    /*
    const columns = [
      {
        Header: "Employee ID",
        accessor: "employee_ID",
      },
      {
        Header: "First Name",
        accessor: "firstName",
      },
      {
        Header: "Last Name",
        accessor: "lastName",
      },
      {
        Header: "Grading",
        accessor: "barGrading",
      },
    ];
*/
    //return <div className="law_report"></div>;
    return (
      <div>
        <ul>
          {laws.map((laws) => (
            <li key={laws.employee_ID}>{laws.firstName}</li>
          ))}
        </ul>
      </div>
    );
    //return <ReactTable columns={columns}></ReactTable>;
  }
}

export default law_report;
