import React, { Component } from "react";
import "./bar.css";
//import Axios from "axios";
import ReactTable from "react-table";
import "react-table/react-table.css";

class bars extends Component {
  constructor(props) {
    super(props);
    this.state = {
      bar: [],
    };
  }

  componentDidMount = () => {
    const url = "/lawyersReporter";
    fetch(url, {
      method: "GET",
    })
      .then((response) => response.json())
      .then((bar) => {
        // console.log("bar", bar);
        this.setState({ bar: bar });
        console.log("laywers", bar);
      });
  };

  render() {
    const { bar } = this.state;

    const columns = [
      {
        Header: "ID",
        accessor: "employee_ID",
      },
      {
        Header: "First Name",
        accessor: "firstName",
      },
      {
        Header: "Last Name",
        accessor: "lastName",
      },
      {
        Header: "Grading",
        accessor: "barGrading",
      },
    ];

    return (
      <div>
        <ul>
          {this.state.posts.map((post) => (
            <p>
              <li>Some Text_1: {post.db_col_1}</li>
              <li>Some Text_2: {post.db_col_2}</li>
              <li>Some Text_3: {post.db_col_3}</li>
            </p>
          ))}
        </ul>
      </div>
    );
    /*
    return (
      <div className="breezy">
        <h2>Lawyers</h2>
        <ReactTable
          columns={columns}
          data={bar}
          defaultPageSize={7}
          //pageSizeOptions={[3, 6]}
          showPagination={true}
        ></ReactTable>
        ;
      </div>
    );
    */
  }
}

export default bars;
