import React, { Component } from "react";
import "./bar.css";
import Axios from "axios";
import ReactTable from "react-table";
import "react-table/react-table.css";

class bars extends Component {
  constructor(props) {
    super(props);
    this.state = {
      bar: [],
    };
  }

  componentDidMount = () => {
    Axios.get("/lawyersReporter").then((response) => {
      this.setState({
        bar: response.data[0],
      });
    });
  };

  render() {
    const { bar } = this.state;
    const columns = [
      {
        Header: "Lawyer Id",
        accessor: "employee_ID",
      },
      {
        Header: "First Name",
        accessor: "firstName",
      },
      {
        Header: "Last Name",
        accessor: "lastName",
      },
      {
        Header: "Grading",
        accessor: "barGrading",
      },
    ];

    return (
      <div className="breezy">
        <h2>Lawyers</h2>
        <ReactTable columns={columns} data={bar[0]}></ReactTable>
      </div>
    );
  }
}

export default bars;
