import React, { Component } from "react";
import Axios from "axios";
import "./law_report.css";
import ReactTable from "react-table";
import "react-table/react-table.css";

class law_report extends Component {
  constructor(props) {
    super(props);
    this.state = {
      laws: [],
    };
  }

  componentDidMount = () => {
    Axios.get("/lawyersReporter").then((response) => {
      console.log(response.data);
      this.setState({
        laws: JSON.stringify(response.data),
      });
    });
  };

  render() {
    const { laws } = this.state.Header;
    console.log(laws);
    const columns = [
      {
        Header: "Employee ID",
        accessor: "employee_ID",
      },
      {
        Header: "First Name",
        accessor: "firstName",
      },
      {
        Header: "Last Name",
        accessor: "lastName",
      },
      {
        Header: "Grading",
        accessor: "barGrading",
      },
    ];

    //return <div className="law_report"></div>;
    //return <div>hello:{this.state.laws}</div>;
    return <ReactTable columns={columns}></ReactTable>;
  }
}

export default law_report;
