import React, { Component } from "react";
import "./bar.css";
//import Axios from "axios";
import ReactTable from "react-table";
import "react-table/react-table.css";

class bars extends Component {
  constructor(props) {
    super(props);
    this.state = {
      bar: [],
    };
  }

  componentDidMount = () => {
    const url = "/lawyersReporter";
    fetch(url, {
      method: "GET",
    })
      .then((response) => response.json())
      .then((bar) => {
        // console.log("bar", bar);
        this.setState({ bar: bar });
        console.log("laywers", bar);
      });
  };
  /*
  render() {
    const { bar } = this.state;

    const columns = [
      {
        Header: "Lawyer Id",
        accessor: "employee_ID",
      },
      {
        Header: "First Name",
        accessor: "firstName",
      },
      {
        Header: "Last Name",
        accessor: "lastName",
      },
      {
        Header: "Grading",
        accessor: "barGrading",
      },
    ];

    return (
      <div className="breezy">
        <h2>Lawyers</h2>
        <ReactTable
          columns={columns}
          data={bar}
          defaultPageSize={7}
          showPagination={true}
        ></ReactTable>
        ;
      </div>
    );
  }
}
*/
  render() {
    return (
      <main className="container my-5">
        <h1 className="text-primary text-center">Current Availability</h1>
        <table className="table table-dark">
          <thead>
            <tr>
              <th scope="col">ID</th>
              <th scope="col">Queue</th>
              <th scope="col">CallDate</th>
              <th scope="col">TimeStart</th>
              <th scope="col">SecsFromMidnight</th>
              <th scope="col">TimeEnd</th>
              <th scope="col">EndSeconds</th>
              <th scope="col">TimeSlotCapacity</th>
              <th scope="col">Available</th>
              <th scope="col">Pending</th>
              <th scope="col">CallsCommitted</th>
            </tr>
          </thead>
          <tbody>
            <td>
              {this.state.availabilities.map((availability) => {
                return <td>{availability.id}</td>;
              })}
            </td>
          </tbody>
        </table>
      </main>
    );
  }
}

export default bars;
