import React, { Component } from "react";
import "./bar.css";
//import Axios from "axios";
import ReactTable from "react-table";
import "react-table/react-table.css";

class bars extends Component {
  constructor(props) {
    super(props);
    this.state = {
      bar: [],
    };
  }

  componentDidMount = () => {
    const url = "/lawyersReporter";
    fetch(url, {
      method: "GET",
    })
      .then((response) => response.json())
      .then((bar) => {
        // console.log("bar", bar);
        this.setState({ bar: bar });
        console.log("laywers", bar);
      });
  };
  //ttyr
  ReactTable({ columns, data }) {
    const {
      getTableProps,
      getTableBodyProps,
      headerGroups,
      rows,
      prepareRow,
    } = useTable({ columns, data });

    // Render Data Table UI
    return (
      <table {...getTableProps()}>
        <thead>
          {headerGroups.map((headerGroup) => (
            <tr {...headerGroup.getHeaderGroupProps()}>
              {headerGroup.headers.map((column) => (
                <th {...column.getHeaderProps()}>{column.render("Header")}</th>
              ))}
            </tr>
          ))}
        </thead>
        <tbody {...getTableBodyProps()}>
          {rows.map((row, i) => {
            prepareRow(row);
            return (
              <tr {...row.getRowProps()}>
                {row.cells.map((cell) => {
                  return (
                    <td {...cell.getCellProps()}>{cell.render("Cell")}</td>
                  );
                })}
              </tr>
            );
          })}
        </tbody>
      </table>
    );
  }

  render() {
    const { bar } = this.state;

    const columns = [
      {
        Header: "ID",
        accessor: "employee_ID",
      },
      {
        Header: "First Name",
        accessor: "firstName",
      },
      {
        Header: "Last Name",
        accessor: "lastName",
      },
      {
        Header: "Grading",
        accessor: "barGrading",
      },
    ];

    return (
      <div className="breezy">
        <h2>Lawyers</h2>
        <ReactTable
          columns={columns}
          data={bar}
          defaultPageSize={7}
          pageSizeOptions={[3, 6]}
          showPagination={true}
        ></ReactTable>
        ;
      </div>
    );
  }
}

export default bars;
