import React, { Component } from "react";
import "./bar.css";
//import Axios from "axios";
import ReactTable from "react-table";
import "react-table/react-table.css";

class bars extends Component {
  constructor(props) {
    super(props);
    this.state = {
      bar: [],
    };
  }

  componentDidMount = () => {
    const url = "/lawyersReporter";
    fetch(url, {
      method: "GET",
    })
      .then((data) => response.json())
      .then((bar) => {
        this.setState({ bar: bar });
        console.log("bar", bar);
      });
  };

  render() {
    /*
    const tableData = [
      {
        employee_ID: 1,
        firstName: "Fred",
        lastName: "Nii-Martley",
        barGrading: 100,
      },
      {
        employee_ID: 2,
        firstName: "George",
        lastName: "Newton",
        barGrading: 200,
      },
      {
        employee_ID: 3,
        firstName: "Cyndy",
        lastName: "Hopson",
        barGrading: 300,
      },
    ];
    */

    const columns = [
      {
        Header: "Lawyer Id",
        accessor: "employee_ID",
      },
      {
        Header: "First Name",
        accessor: "firstName",
      },
      {
        Header: "Last Name",
        accessor: "lastName",
      },
      {
        Header: "Grading",
        accessor: "barGrading",
      },
    ];

    return (
      <div className="breezy">
        <h2>Lawyers</h2>
        <ReactTable columns={columns} data={this.state.bar}></ReactTable>
      </div>
    );
  }
}

export default bars;
