import React, { Component } from "react";
import "./bar.css";
import Axios from "axios";

class bars extends Component {
  constructor(props) {
    super(props);
    this.state = {
      bar: [],
    };
  }

  componentDidMount = (props) => {
    Axios.get("/lawyersReporter").then((response) => {
      console.log(response.data);
      this.setState({
        bar: JSON.stringify(response.data),
      });
    });
  };

  render() {
    const columns = [
      {
        Header: "Employee ID",
        accessor: "employee_ID",
      },
      {
        Header: "First Name",
        accessor: "firstName",
      },
      {
        Header: "Last Name",
        accessor: "lastName",
      },
      {
        Header: "Grading",
        accessor: "barGrading",
      },
    ];
    return (
      <div>
        <h2>Lawyers</h2>
        <ReactTable columns={columns}></ReactTable>;
      </div>
    );
  }
}

export default bars;
