import React, { Component } from "react";
import Axios from "axios";
import "./law_report.css";

class law_report extends Component {
  constructor(props) {
    super(props);
    this.state = {
      kind: "",
      data: [],
    };
  }
  componentDidMount = (props) => {
    Axios.get("/lawyersReporter").then(({ data }) => {
      //console.log(response.data.tem_c);
      //console.log(response);
      this.setState({
        kind: data.kind,
        data: data.data.children,
      });
    });
  };

  render() {
    var { laws } = this.state;

    /*
    const columns = [
      {
        Header: "Employee ID",
        accessor: "employee_ID",
      },
      {
        Header: "First Name",
        accessor: "firstName",
      },
      {
        Header: "Last Name",
        accessor: "lastName",
      },
      {
        Header: "Grading",
        accessor: "barGrading",
      },
    ];
*/
    //return <div className="law_report"></div>;
    return (
      <div>
        <ul>
          {laws.map((laws) => (
            <li key={laws.id}>{laws.firstName}</li>
          ))}
        </ul>
      </div>
    );
    //return <ReactTable columns={columns}></ReactTable>;
  }
}

export default law_report;
